package week1.day1;

public class OddEeven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 7;
		
		if(n%2==0)
		{
			System.out.println(n+ " is a even number");
		}
		else
		{
			System.out.println(n+ " is a odd number");
		}
			
	}

}
